import React, { useState } from 'react';
// Import Modal if it's in a separate file
import Modal from './Modal';

function App() {
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedService, setSelectedService] = useState('');

  const services = [
    { name: "Home Repair", description: "Expert solutions for your home repair needs." },
    { name: "Electrical Work", description: "Safe and reliable electrical services for your home or office." },
    // Add other services here...
  ];

  const openModal = (service) => {
    setSelectedService(service);
    setIsModalOpen(true);
  };

  return (
    <div className="App">
      {/* Navigation and other components */}
      <main>
        <section id="services" className="section">
          <h2>Our Services</h2>
          <div className="service-list">
            {services.map((service, index) => (
              <div key={index} className="service-item" onClick={() => openModal(service)}>
                <h3>{service.name}</h3>
                <p>{service.description}</p>
              </div>
            ))}
          </div>
        </section>
        <Modal isOpen={isModalOpen} onClose={() => setIsModalOpen(false)}>
          <h2>{selectedService.name}</h2>
          <p>{selectedService.description}</p>
        </Modal>
        {/* Contact form and other sections */}
      </main>
    </div>
  );
}
